package com.lam.coder.rosalind;

import org.junit.After;
import org.junit.Assert;
import org.junit.Before;
import org.junit.Test;

import com.lam.coder.rosalind.CountingPointMutations;

public class CountingPointMutationsTest {
	CountingPointMutations countingPointMutations;
	
	@Before
	public void setUp() throws Exception {
		this.countingPointMutations = new CountingPointMutations();
	}

	@After
	public void tearDown() throws Exception {
		this.countingPointMutations  = null;
	}


	@Test
	public void test() {
		Assert.assertEquals(7, this.countingPointMutations.hammingDistance("GAGCCTACTAACGGGAT", "CATCGTAATGACGGCCT"));
		Assert.assertEquals(498, this.countingPointMutations.hammingDistance("", 
		""));
//		Assert.assertEquals(0, this.countingPointMutations.hammingDistance("", ""));
	}
}
